 <?php $__env->startSection('title', '| All Posts'); ?> <?php $__env->startSection('stylesheet'); ?> <style> .tags span { margin: 0px 5px; } .tags{ display:flex; } </style> <?php $__env->stopSection(); ?> <?php $__env->startSection('content'); ?> <div class="row"> <div class="col-md-6"> <h3>All Portfolios</h3> </div> <div class="col-md-6"> <a href="<?php echo e(route('portfolios.create')); ?>" class="btn btn-success btn-md pull-right btn-top-margin" >Create new portfolio</a> </div> </div> <div class="row section-center" id="grid"> <table class="table table-hover"> <thead> <tr> <th>Imgae</th> <th>Title</th> <th>Content</th> <th>Tags</th> <th width="150px">Created At</th> <th width="150px">Updated At</th> <th >Action</th> </tr> </thead> <tbody> <?php $__currentLoopData = $portfolios; $__env->addLoop($__currentLoopData); foreach($__currentLoopData as $portfolio): $__env->incrementLoopIndices(); $loop = $__env->getLastLoop(); ?> <tr> <td><img src="<?php echo e(asset($portfolio->imageurl)); ?>" class="img-responsive" alt="Image"></td> <td style="min-width:150px" ><?php echo e($portfolio->title); ?></td> <td><?php echo e(substr($portfolio->body , 0 ,250)); ?> <?php echo e(strlen($portfolio->body) > 250 ? "..." : ""); ?></td> <td> <div class="tags"> <?php $__currentLoopData = $portfolio->tags; $__env->addLoop($__currentLoopData); foreach($__currentLoopData as $tag): $__env->incrementLoopIndices(); $loop = $__env->getLastLoop(); ?> <span class="badge"><?php echo e($tag->name); ?></span> <?php endforeach; $__env->popLoop(); $loop = $__env->getLastLoop(); ?> </div> </td> <td><?php echo e(date('jS M , h:ia' , strtotime($portfolio->created_at))); ?></td> <td><?php echo e(date('jS M, h:ia' , strtotime($portfolio->updated_at))); ?></td> <td> <?php echo Html::linkRoute('portfolios.edit' , 'Edit' , array($portfolio->id) , array('class'=>'btn btn-info btn-block') ); ?> <?php echo Html::linkRoute('portfolios.show' , 'View' , array($portfolio->id) , array('class'=>'btn btn-success btn-block') ); ?> </td> </tr> <?php endforeach; $__env->popLoop(); $loop = $__env->getLastLoop(); ?> </tbody> </table> </div> <div class="text-center"> <?php echo $portfolios->links(); ?> </div> <?php $__env->stopSection(); ?>
